mapper.update:
UPDATE TBL_NOTICE
SET HITCOUNT = (HITCOUNT+1)
WHERE NOTICE_CODE = #{notice_code}

UPDATE TBL_NOTICE
SET NOTICE_TITLE = #{notice_title}, NOTICE_CONTENT = #{notice_content}
WHERE NOTICE_CODE = #{notice_code}

mapper.select:
SELECT NOTICE_TITLE, NOTICE_CONTENT, NOTICE_CODE
FROM TBL_NOTICE
WHERE NOTICE_CODE = #{notice_code}
